USE turistear;

CREATE TABLE `typeOfCulture` (
	`id` INT auto_increment PRIMARY KEY,
    `type`	VARCHAR(512)
);

    
CREATE TABLE `culture` (
	`id` INT auto_increment PRIMARY KEY,
    `id_province` INT,
    `id_type` INT,
    `title`	VARCHAR(512),
    `images` VARCHAR(512),
    `description` text,
    FOREIGN KEY (id_province) REFERENCES province(id),
    FOREIGN KEY (id_type) REFERENCES typeOfCulture(id)
);

